import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Modal, Button } from 'react-bootstrap';
import Checkout from 'Checkout';


const ViewCart = ({ cartItems = [], handleRemoveFromCart }) => {
  const [shippingDetails, setShippingDetails] = useState({
    name: '',
    address: '',
    email: ''
  });

  const [showShippingModal, setShowShippingModal] = useState(false);
  const [showCheckout, setShowCheckout] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setShippingDetails({ ...shippingDetails, [name]: value });
  };

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => {
      const numericPrice = parseFloat(item.price.replace(/[^0-9.-]+/g, ''));
      return total + numericPrice * item.quantity;
    }, 0).toFixed(2);
  };

  const handleCheckoutClick = () => {
    const { name, address, email } = shippingDetails;
    if (name && address && email) {
      setShowShippingModal(false); // Close the modal
      setShowCheckout(true); // Show the checkout component
    } else {
      alert('Please enter all shipping information.');
    }
  };

  const handleConfirmCheckout = () => {
    if (window.confirm('Do you really want to continue checking out?')) {
      // Implement checkout logic here
      alert('Checked out successfully!');
      // Optionally clear cart or handle post-checkout state
    }
  };

  return (
    <div>
      <center>
        {cartItems.length === 0 ? (
          <p>Your cart is empty</p>
        ) : (
          <>
            <h2>View Cart</h2>
            <ul className="product-card-body list-group col-sm-8">
              <li className="list-group-item">
                <div className="row">
                  <b className="col-sm-4">Name</b>
                  <b className="col-sm-2">Price</b>
                  <b className="col-sm-2">Quantity</b>
                  <b className="col-sm-2">Remove</b>
                </div>
              </li>
              {cartItems.map((item) => (
                <li className="list-group-item" key={item.id}>
                  <div className="row">
                    <div className="col-sm-4">{item.name}</div>
                    <div className="col-sm-2">{item.price}</div>
                    <div className="col-sm-2">{item.quantity}</div>
                    <button type="button" className="col-sm-2 btn btn-danger btn-sm" onClick={() => handleRemoveFromCart(item.id)}>Remove</button>
                  </div>
                </li>
              ))}
            </ul>
            <p>Total Price: ${calculateTotalPrice()}</p>
            <div>
              <Button variant="primary" onClick={() => setShowShippingModal(true)}>Proceed to Checkout</Button>
            </div>
          </>
        )}
      </center>

      <Modal show={showShippingModal} onHide={() => setShowShippingModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Enter Shipping Details</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form>
            <div className="form-group">
              <label>Name</label>
              <input type="text" className="form-control" name="name" value={shippingDetails.name} onChange={handleInputChange} />
            </div>
            <div className="form-group">
              <label>Address</label>
              <input type="text" className="form-control" name="address" value={shippingDetails.address} onChange={handleInputChange} />
            </div>
            <div className="form-group">
              <label>Email</label>
              <input type="email" className="form-control" name="email" value={shippingDetails.email} onChange={handleInputChange} />
            </div>
          </form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowShippingModal(false)}>Close</Button>
          <Button variant="primary" onClick={handleCheckoutClick}>Checkout</Button>
        </Modal.Footer>
      </Modal>

      {showCheckout && (
        <Checkout
          cartItems={cartItems}
          name={shippingDetails.name}
          address={shippingDetails.address}
          email={shippingDetails.email}
          totalPrice={calculateTotalPrice()}
          handleConfirmCheckout={handleConfirmCheckout}
        />
      )}
    </div>
  );
};

ViewCart.propTypes = {
  cartItems: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number.isRequired,
      name: PropTypes.string.isRequired,
      price: PropTypes.string.isRequired,
      quantity: PropTypes.number.isRequired
    })
  ).isRequired,
  handleRemoveFromCart: PropTypes.func.isRequired
};

export default ViewCart;
