import React from 'react';
import PropTypes from 'prop-types';

const Checkout = ({ shippingDetails, totalPrice, handleConfirmCheckout }) => {
  const handleConfirm = () => {
    handleConfirmCheckout(); 
  };

  return (
    <div className="container">
      <h2>Checkout</h2>
      <div className="card">
        <div className="card-body">
          <h3 className="card-title">Shipping Details</h3>
          <p className="card-text">Name: {shippingDetails.name}</p>
          <p className="card-text">Address: {shippingDetails.address}</p>
          <p className="card-text">Email: {shippingDetails.email}</p>
        </div>
      </div>
      <div className="card mt-3">
        <div className="card-body">
          <h3 className="card-title">Total Price: ${totalPrice}</h3>
        </div>
      </div>
      <button className="btn btn-primary mt-3" onClick={handleConfirm}>Confirm Checkout</button>
    </div>
  );
};

Checkout.propTypes = {
    shippingDetails: PropTypes.shape({
      name: PropTypes.string.isRequired,
      address: PropTypes.string.isRequired,
      email: PropTypes.string.isRequired
    }).isRequired,
    totalPrice: PropTypes.number.isRequired,
    handleConfirmCheckout: PropTypes.func.isRequired 
};

export default Checkout;
