import React from 'react';
import PropTypes from 'prop-types';

const Checkout = ({ cartItems, name, address, email, totalPrice, handleConfirmCheckout, handleCancel }) => {
  const handleConfirm = () => {
    handleConfirmCheckout(); 
  };

  const handleCancelClick = () => {
    handleCancel();
  };

  return (
    <div className="container">
      <h2>Checkout</h2>
      <div className="card">
        <div className="card-body">
          <h3 className="card-title">Shipping Details</h3>
          <p className="card-text">Name: {name}</p>
          <p className="card-text">Address: {address}</p>
          <p className="card-text">Email: {email}</p>
        </div>
      </div>
      <div className="card mt-3">
        <div className="card-body">
          <h3 className="card-title">Cart Summary</h3>
          <ul>
            {cartItems.map(item => (
              <li key={item.id}>
                {item.name} - Quantity: {item.quantity} - Price: ${item.price}
              </li>
            ))}
          </ul>
        </div>
      </div>
      <div className="card mt-3">
        <div className="card-body">
          <h3 className="card-title">Total Price: ${totalPrice}</h3>
        </div>
      </div>
      <div className="btn-group mt-3">
        <button className="btn btn-primary" onClick={handleConfirm}>Confirm Checkout</button>
        <button className="btn btn-secondary" onClick={handleCancelClick}>Cancel</button>
      </div>
    </div>
  );
};

Checkout.propTypes = {
  cartItems: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number.isRequired,
      name: PropTypes.string.isRequired,
      price: PropTypes.string.isRequired,
      quantity: PropTypes.number.isRequired
    })
  ).isRequired,
  name: PropTypes.string.isRequired,
  address: PropTypes.string.isRequired,
  email: PropTypes.string.isRequired,
  totalPrice: PropTypes.number.isRequired,
  handleConfirmCheckout: PropTypes.func.isRequired,
  handleCancel: PropTypes.func.isRequired
};

export default Checkout;
