import React from 'react';
import axios from 'axios';
import {useState, useEffect } from "react";
import AddProductButton from './AddProductButton';

export default function Shopping(){
  
  const handleAddToCart = (product) => {
    const existingItem = cartItems.find((item) => item.id === product.id);

    if (existingItem) {
      const updatedCartItems = cartItems.map((item) =>
        item.id === product.id ? { ...item, quantity: item.quantity + product.quantity } : item
      );
      setCartItems(updatedCartItems);
    } else {
      setCartItems([...cartItems, { ...product, quantity: product.quantity }]);
    }
  };
  return(
    <div className="container mt-4">
    <div className="row">
      {product.map(product => (
         <div className="col-sm-6 col-md-3" key={product.id}>
         <div className="card mb-3 product-card">
           <div className="card-body">
             <h5 className="card-title">{product.name}</h5>
             <p className="card-text">{product.description}</p>
             <p className="card-text">â‚±{product.price}</p>
             <AddProductButton className="btn btn-primary btn-sm" onAddToCart={handleAddToCart} />
           </div>
         </div>
       </div>
      ))}
    </div>
  </div>
  )
}
