import React from 'react';
import http from "../http";
import {useState, useEffect } from "react";

export default function Shopping(){
  const [users, setUsers] = useState([]);

  useEffect(()=> {
      fetchAllUsers();
  },[]);

  const fetchAllUsers = () =>{
      http.get('127.0.0.1/api/products').then(res=>{
          setUsers(res.data);
      })
  }
  return(
      <div>
          <h2>Users...</h2>
          <table className="table">
          <thead>
              <tr>
                  <th>ID</th>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Action</th>
              </tr>
          </thead>
          <tbody>
              {users.map((user,index)=>(
                  <tr key={user.id}>
                      <td>{++index}</td>
                      <td>{user.name}</td>
                      <td>{user.email}</td>
                      <td>edit</td>
                  </tr>
              ))}
          </tbody>
      </table>
      </div>
  )
}
