import React from 'react';

const Checkout = ({ shippingDetails, totalPrice, handleConfirmCheckout }) => {
  return (
    <div className="container">
      <h2>Checkout</h2>
      <div className="card">
        <div className="card-body">
          <h5 className="card-title">Shipping Details</h5>
          <p className="card-text">Name: {shippingDetails.name}</p>
          <p className="card-text">Address: {shippingDetails.address}</p>
          <p className="card-text">Email: {shippingDetails.email}</p>
        </div>
      </div>
      <div className="card mt-3">
        <div className="card-body">
          <h5 className="card-title">Order Summary</h5>
          <p className="card-text">Total Price: ${totalPrice}</p>
        </div>
      </div>
      <button className="btn btn-primary mt-3" onClick={handleConfirmCheckout}>Confirm Checkout</button>
    </div>
  );
};

export default Checkout;
